contracts/DynamicInvoiceToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.27;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./request-network/interfaces/IEthereumProxy.sol";

import "./interfaces/dynamic-invoice-token/IDynamicInvoiceToken.sol";
import "./interfaces/IDynamicInvoiceTokenDeployer.sol";
import "./interfaces/IPayChunkRegistry.sol";

import "./libraries/ErrorLibrary.sol";
import "./DynamicInvoiceTokenFactory.sol";

/**
 * @title DynamicInvoiceToken
 * @notice This contract supports to quickly store data of an invoice including reference data,
 *         root and children.
 */
contract DynamicInvoiceToken is
    IDynamicInvoiceToken,
    ERC721,
    AccessControl,
    ReentrancyGuard
{
    /// @notice Status of the invoice
    enum InvoiceStatus {
        PENDING,
        PARTIALLY_PAID,
        PAID,
        CANCELLED
    }

    /// @notice Role for the contract
    bytes32 public constant CONTRACT_ROLE = keccak256("CONTRACT_ROLE");

    /// @notice Role for the payer of the invoice
    bytes32 public constant PAYER_ROLE = keccak256("PAYER_ROLE");

    /// @notice Role for the payee of the invoice
    bytes32 public constant PAYEE_ROLE = keccak256("PAYEE_ROLE");

    /// @inheritdoc IDynamicInvoiceTokenState
    string public override requestId;

    /// @inheritdoc IDynamicInvoiceTokenState
    bytes public override paymentReference;

    /// @inheritdoc IDynamicInvoiceTokenState
    address public override payer;

    /// @inheritdoc IDynamicInvoiceTokenState
    address public override payee;

    /// @inheritdoc IDynamicInvoiceTokenState
    uint256 public override amount;

    /// @inheritdoc IDynamicInvoiceTokenState
    uint256 public override amountPaid = 0;

    /// @dev All child of the dynamic invoice token
    address[] private _children;

    /// @inheritdoc IDynamicInvoiceTokenImmutables
    address public override factory;

    /// @inheritdoc IDynamicInvoiceTokenImmutables
    address public override registry;

    /// @inheritdoc ERC165
    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override(ERC721, AccessControl) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    /// @dev Receive function
    receive() external payable {
        amountPaid += msg.value;
    }

    constructor(string memory name, string memory symbol) ERC721(name, symbol) {
        (
            registry,
            factory,
            name,
            symbol,
            requestId,
            paymentReference,
            payer,
            payee,
            amount
        ) = IDynamicInvoiceTokenDeployer(msg.sender).parameters();

        _grantRole(PAYER_ROLE, payer);
        _grantRole(PAYEE_ROLE, payee);
        _grantRole(CONTRACT_ROLE, address(this));
    }

    /// @inheritdoc IDynamicInvoiceTokenState
    function children() external view override returns (address[] memory) {
        return _children;
    }

    /// @inheritdoc IDynamicInvoiceTokenState
    function status() external view override returns (uint8) {
        if (amountPaid == 0) {
            return uint8(InvoiceStatus.PENDING);
        }
        if (amountPaid < amount) {
            return uint8(InvoiceStatus.PARTIALLY_PAID);
        }
        if (amountPaid >= amount) {
            return uint8(InvoiceStatus.PAID);
        }

        return uint8(InvoiceStatus.CANCELLED);
    }

    /// @inheritdoc IDynamicInvoiceTokenActions
    function spawnChild(
        string calldata _requestId,
        bytes calldata _paymentReference,
        address _payer,
        uint256 _amount
    ) external override onlyRole(PAYER_ROLE) returns (address) {
        require(_payer != address(0), "DynamicInvoiceToken: Invalid payer");
        require(_amount > 0, "DynamicInvoiceToken: Invalid amount");

        address childAddress = DynamicInvoiceTokenFactory(factory)
            .createDynamicInvoiceToken(
                name(),
                symbol(),
                _requestId,
                _paymentReference,
                _payer,
                address(this),
                _amount
            );

        _children.push(childAddress);

        emit DynamicInvoiceTokenSpawned(address(this), childAddress);

        return childAddress;
    }

    /// @inheritdoc IDynamicInvoiceTokenActions
    function pay() external payable override onlyRole(PAYER_ROLE) nonReentrant {
        require(
            this.status() != uint8(InvoiceStatus.PAID),
            "DynamicInvoiceToken: The invoice is already paid"
        );
        require(
            this.status() != uint8(InvoiceStatus.CANCELLED),
            "DynamicInvoiceToken: The invoice is cancelled"
        );
        require(
            msg.value >= amount - amountPaid,
            "DynamicInvoiceToken: The amount is not enough"
        );

        address ethereumProxy = IPayChunkRegistry(registry).contracts(
            "ETHEREUM_PROXY"
        );

        require(
            isContract(ethereumProxy),
            "DynamicInvoiceToken: ethereumProxy is not a contract"
        );

        /// @notice Transfer the amount to invoice before delegate call to EthereumProxy
        // payable(address(this)).transfer(msg.value);

        try
            IEthereumProxy(ethereumProxy).transferWithReference{
                value: msg.value
            }(payable(payee), paymentReference)
        {
            amountPaid += msg.value;
        } catch Error(string memory reason) {
            revert(string(abi.encodePacked("DynamicInvoiceToken: ", reason)));
        } catch {
            revert("DynamicInvoiceToken: Payment failed");
        }

        emit DynamicInvoiceTokenPaid(address(this));
    }

    function isContract(address _addr) internal view returns (bool) {
        uint32 size;
        assembly {
            size := extcodesize(_addr)
        }
        return (size > 0);
    }
}

contracts/DynamicInvoiceTokenDeployer.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.27;

import "./interfaces/IDynamicInvoiceTokenDeployer.sol";
import "./DynamicInvoiceToken.sol";

/**
 * @title DynamicInvoiceTokenDeployer
 */
contract DynamicInvoiceTokenDeployer is IDynamicInvoiceTokenDeployer {
    struct Parameters {
        address registry;
        address factory;
        string name;
        string symbol;
        string requestId;
        bytes paymentReference;
        address payer;
        address payee;
        uint256 amount;
    }

    /// @inheritdoc IDynamicInvoiceTokenDeployer
    Parameters public override parameters;

    /// @dev Deploy a dynamic invoice token
    /// @param _registry The address of the registry
    /// @param _factory The address of the factory
    /// @param _name The name of the token
    /// @param _symbol The symbol of the token
    /// @param _paymentReference The payment reference of the invoice
    /// @param _payer The payer of the invoice
    /// @param _payee The payee of the invoice
    /// @param _amount The amount of the invoice
    function deploy(
        address _registry,
        address _factory,
        string memory _name,
        string memory _symbol,
        string memory _requestId,
        bytes memory _paymentReference,
        address _payer,
        address _payee,
        uint256 _amount

    ) internal returns (address dynamicInvoiceToken) {
        parameters = Parameters({
            registry: _registry,
            factory: _factory,
            name: _name,
            symbol: _symbol,
            requestId: _requestId,
            paymentReference: _paymentReference,
            payer: _payer,
            payee: _payee,
            amount: _amount
        });
        dynamicInvoiceToken = address(
            new DynamicInvoiceToken{salt: keccak256(abi.encode(_paymentReference))}(
                _name,
                _symbol
            )
        );
        delete parameters;
    }
}

contracts/DynamicInvoiceTokenFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.27;

import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";

import "./interfaces/IDynamicInvoiceTokenFactory.sol";

import "./DynamicInvoiceToken.sol";
import "./DynamicInvoiceTokenDeployer.sol";
import "./PayChunkRegistry.sol";

/**
 * @title DynamicInvoiceTokenFactory
 * @notice This contract supports to create dynamic invoice token
 */
contract DynamicInvoiceTokenFactory is
    IDynamicInvoiceTokenFactory,
    DynamicInvoiceTokenDeployer,
    AccessControl
{
    /// @notice Role for the factory
    bytes32 public constant OWNER_ROLE = keccak256("OWNER_ROLE");

    /// @inheritdoc IDynamicInvoiceTokenFactory
    address public override owner;

    /// @inheritdoc IDynamicInvoiceTokenFactory
    mapping(bytes => address) public override getDynamicInvoiceToken;

    /// @dev PayChunkRegistry contract
    address public registry;

    constructor(address _registry) {
        _grantRole(OWNER_ROLE, msg.sender);

        owner = msg.sender;
        registry = _registry;

        emit OwnerChanged(address(0), msg.sender);
    }

    /// @inheritdoc IDynamicInvoiceTokenFactory
    function createDynamicInvoiceToken(
        string memory _name,
        string memory _symbol,
        string calldata _requestId,
        bytes calldata _paymentReference,
        address _payer,
        address _payee,
        uint256 _amount
    ) external override returns (address dynamicInvoiceToken) {
        require(
            getDynamicInvoiceToken[_paymentReference] == address(0),
            "DynamicInvoiceTokenFactory: The dynamic invoice token is already created"
        );

        require(
            _payer != address(0) && _payee != address(0),
            "DynamicInvoiceTokenFactory: Payer and payee must not be zero address"
        );

        dynamicInvoiceToken = deploy(
            registry,
            address(this),
            _name,
            _symbol,
            _requestId,
            _paymentReference,
            _payer,
            _payee,
            _amount
        );

        getDynamicInvoiceToken[_paymentReference] = dynamicInvoiceToken;

        emit DynamicInvoiceTokenCreated(dynamicInvoiceToken, _paymentReference);
    }

    /// @inheritdoc IDynamicInvoiceTokenFactory
    function setOwner(address _owner) external override onlyRole(OWNER_ROLE) {
        require(
            hasRole(OWNER_ROLE, msg.sender),
            "DynamicInvoiceTokenFactory: Must have OWNER_ROLE to set owner"
        );

        address oldOwner = owner;
        owner = _owner;

        emit OwnerChanged(oldOwner, _owner);
    }
}

contracts/interfaces/dynamic-invoice-tok.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.27;

import "./IDynamicInvoiceTokenState.sol";
import "./IDynamicInvoiceTokenEvents.sol";
import "./IDynamicInvoiceTokenActions.sol";
import "./IDynamicInvoiceTokenImmutables.sol";

/**
 * @title IDynamicInvoiceToken - Interface for Dynamic Invoice Token
 */
interface IDynamicInvoiceToken is
    IDynamicInvoiceTokenState,
    IDynamicInvoiceTokenActions,
    IDynamicInvoiceTokenEvents,
    IDynamicInvoiceTokenImmutables
{

}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.27;

/**
 * @title IDynamicInvoiceTokenActions - Interface for Dynamic Invoice Token Actions
 */
interface IDynamicInvoiceTokenActions {
    /// @notice Spawn a new child dynamic invoice token
    /// @param _requestId Request ID of child invoice that be assigned
    /// @param _paymentReference Payment reference of child invoice that be assigned
    /// @param _payer Payer of child invoice that be assigned
    /// @param _amount Amount of child invoice that be assigned
    function spawnChild(
        string calldata _requestId,
        bytes calldata _paymentReference,
        address _payer,
        uint256 _amount
    ) external returns (address);

    /// @notice Pay the invoice, using EthereumProxy of Request Network
    function pay() external payable;
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.27;

/**
 * @title IDynamicInvoiceTokenEvents - Interface for Dynamic Invoice Token Events
 */
interface IDynamicInvoiceTokenEvents {
    /// @notice Event emitted when the invoice is paid
    /// @param invoice Address of the invoice
    event DynamicInvoiceTokenPaid(address indexed invoice);

    /// @notice Event emitted when the invoice is canceled
    /// @param invoice Address of the invoice
    event DynamicInvoiceTokenCanceled(address indexed invoice);

    /// @notice Event emitted when the invoice is spawned
    /// @param invoice Address of the invoice
    /// @param childInvoice Address of the child invoice
    event DynamicInvoiceTokenSpawned(address indexed invoice, address indexed childInvoice);

    /// @notice Event emitted when the invoice update progresses
    /// @param invoice Address of the invoice
    /// @param progress Progress of the update
    event DynamicInvoiceTokenUpdateProgress(address indexed invoice, uint8 progress);

}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.27;

/**
 * @title IDynamicInvoiceTokenImmutables - Interface for Dynamic Invoice Token Immutables
 */
interface IDynamicInvoiceTokenImmutables {
    /// @notice Manage all contracts be able to interact
    function registry() external view returns (address);

    /// @notice The contract of factory that deployed the token
    function factory() external view returns (address);
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.27;

/**
 * @title IDynamicInvoiceTokenState - Interface for Dynamic Invoice Token State
 */
interface IDynamicInvoiceTokenState {
    /// @notice Request ID of the invoice
    function requestId() external view returns (string memory);

    /// @notice Reference to the request payment, for handle the payment (Request Network)
    function paymentReference() external view returns (bytes memory);

    /// @notice Get the payee of the invoice
    function payee() external view returns (address);

    /// @notice Get the payer of the invoice
    function payer() external view returns (address);

    /// @notice Get the amount of the invoice
    function amount() external view returns (uint256);

    /// @notice Get the paid amount of the invoice
    function amountPaid() external view returns (uint256);

    /// @notice All chilldren of the invoice
    function children() external view returns (address[] memory);

    /// @notice Get the status of the invoice, must be in ["PENDING", "PARTIALLY_PAID", "PAID", "CANCELLED"]
    function status() external view returns (uint8);
}

contracts/interfaces/IDynamicInvoiceToke.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.27;

/**
 * @title IDynamicInvoiceTokenDeployer
 * @notice A contract that constructs the parameters for the deployment of the Dynamic Invoice Token
 */
interface IDynamicInvoiceTokenDeployer {
    /// @notice Get the parameters to be used for the deployment of the dynamic invoice token
    /// @return registry The address of the registry
    /// @return factory The address of the factory
    /// @return name The name of the token
    /// @return symbol The symbol of the token
    /// @return requestId The request ID of the invoice
    /// @return paymentReference The payment reference of the invoice
    /// @return payer The payer of the invoice
    /// @return payee The payee of the invoice
    /// @return amount The amount of the invoice
    function parameters()
        external
        view
        returns (
            address registry,
            address factory,
            string memory name,
            string memory symbol,
            string memory requestId,
            bytes calldata paymentReference,
            address payer,
            address payee,
            uint256 amount
        );
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.27;

/**
 * @title IDynamicInvoiceTokenFactory
 * @notice A contract that creates dynamic invoice tokens
 */
interface IDynamicInvoiceTokenFactory {
    /// @notice This event is emitted when a dynamic invoice token is created
    /// @param dynamicInvoiceToken The address of the dynamic invoice token
    /// @param paymentReference The payment reference of the invoice
    event DynamicInvoiceTokenCreated(
        address indexed dynamicInvoiceToken,
        bytes paymentReference
    );

    /// @notice Emitted when the owner of the factory is changed
    /// @param oldOwner The owner before the owner was changed
    /// @param newOwner The owner after the owner was changed
    event OwnerChanged(address indexed oldOwner, address indexed newOwner);

    /// @notice Returns the current owner of the factory
    /// @dev Can be changed by the current owner via setOwner
    /// @return The address of the factory owner
    function owner() external view returns (address);

    /// @notice This function creates a dynamic invoice token
    /// @param _name The name of the token
    /// @param _symbol The symbol of the token
    /// @param _requestId The request ID of the invoice
    /// @param _paymentReference The payment reference of the invoice
    /// @param _payer The payer of the invoice
    /// @param _payee The payee of the invoice
    /// @param _amount The amount of the invoice
    /// @return The address of the dynamic invoice token
    function createDynamicInvoiceToken(
        string memory _name,
        string memory _symbol,
        string calldata _requestId,
        bytes calldata _paymentReference,
        address _payer,
        address _payee,
        uint256 _amount
    ) external returns (address);

    /// @notice Get the address of the dynamic invoice token
    /// @param _paymentReference The payment reference of the invoice
    /// @return The address of the dynamic invoice token
    function getDynamicInvoiceToken(
        bytes calldata _paymentReference
    ) external view returns (address);

    /// @notice Updates the owner of the factory
    /// @dev Must be called by the current owner
    /// @param _owner The new owner of the factory
    function setOwner(address _owner) external;
}

